mapper.update:
UPDATE `wms_ware_sku`
SET stock=stock + #{skuNum}
WHERE sku_id = #{skuId}
  AND ware_id = #{wareId}

mapper.select:
SELECT SUM(stock - stock_locked)
FROM `wms_ware_sku`
WHERE sku_id = #{skuId}

mapper.update:
update wms_ware_sku
set stock_locked = stock_locked + #{num}
where sku_id = #{skuId}
  and ware_id = #{wareId}
  and stock - stock_locked >= #{num}

UPDATE wms_ware_sku
SET stock_locked = stock_locked + #{num}
WHERE sku_id = #{skuId}
  AND ware_id = #{wareId}
  AND stock - stock_locked > 0

update wms_ware_sku
set stock_locked = stock_locked - #{num}
where sku_id = #{skuId}
  and ware_id = #{wareId}
